import { Component } from '@angular/core';

@Component({
  selector: 'app-style-class',
  templateUrl: './style-class.component.html',
  styleUrls: ['./style-class.component.css']
})
export class StyleClassComponent {
 enabled = true;

 myClass = {
  class1 : {},
  class2 : {},
  class3 : {}
 }

 myBorder = "5px solid black";

 myStyle = {
   "border" : "5px solid black",
   "background-color" : ""
 }
}
<div class="box" [class.myClass] = "enabled"></div><br>
<div class="box" [ngClass] = "myClass"></div><br>
<div class="box" [style.border] = "myBorder"></div><br>
<div class="box" [ngStyle] = "myStyle"></div><br>
