import { Component } from '@angular/core';

@Component({
  selector: 'app-test',
  templateUrl: './test.component.html',
  styleUrls: ['./test.component.css']
})
export class TestComponent {

public name = "rutvik";

public myid = "myID-test";

public isDisabled = false;

public component_class = "text-danger";

public hasError = true;

public messageClass = {
  "text-danger" : this.hasError,
  "text-special" : !this.hasError
};

getName (){
  return "Name via function is" + " " +this.name;
}



}
<!-- interpolation -->

<p>test Component</p>

<p>Name is {{name}}</p>

<p>{{getName()}}</p>

<p>{{name.length}}</p>

<p>{{name.toUpperCase()}}</p>

<p>{{2 + 2}}</p>

<!-- property binding -->

<input type="text" [id]="myid" [value] = "name"><br>
<input type="text" [id]="myid" [value] = "name" [disabled] = "false"><br>
<input type="text" [id]="myid" [value] = "name" [disabled] = "isDisabled">

<!-- class binding -->

<h2 class="text-danger">Class</h2>

<h2 [class]="component_class">component Class</h2>

<h2  class="text-special" [class]="component_class" >component Class</h2>

<h2 [class.text-primary] = "hasError">condition class</h2>


<!-- more than one class binding on condition using ngClass -->
<h2 [ngClass] = "messageClass">more Than One Class</h2>
