import { Component } from '@angular/core';

@Component({
  selector: 'app-test',
  templateUrl: './test.component.html',
  styleUrls: ['./test.component.css']
})
export class TestComponent {

public name = "rutvik";

public myid = "myID-test";

public isDisabled = false;

public component_class = "text-danger";

public hasError = true;

public text_color = "red";

public buttonClick = " "; 

public messageClass = {
  "text-danger" : this.hasError,
  "text-special" : !this.hasError
};

public messageStyle = {
  color:'red',
  fontStyle : "italic"
}

getName (){
  return "Name via function is" + " " +this.name;
}

Onclick (){
  console.log("button clicked");
  this.buttonClick = "data will be added on click into property";
}

OnClick (event : any){
  console.log(event);
  this.buttonClick = event.type;
}

template(){
  console.log(inputReference.value)
}

}
<!-- interpolation -->

<p>test Component</p>

<p>Name is {{name}}</p>

<p>{{getName()}}</p>

<p>{{name.length}}</p>

<p>{{name.toUpperCase()}}</p>

<p>{{2 + 2}}</p>

<!-- property binding -->

<input type="text" [id]="myid" [value] = "name"><br>
<input type="text" [id]="myid" [value] = "name" [disabled] = "false"><br>
<input type="text" [id]="myid" [value] = "name" [disabled] = "isDisabled">

<!-- class binding -->

<h2 class="text-danger">Class</h2>

<h2 [class]="component_class">component Class</h2>

<h2  class="text-special" [class]="component_class" >component Class</h2>

<h2 [class.text-primary] = "hasError">condition class</h2>


<!-- more than one class binding on condition using ngClass -->

<h2 [ngClass] = "messageClass">more Than One Class</h2>


<!-- style binding -->

<h2 style="color: red;">Style bindinng</h2>

<h2 [style.color]= "'red'">Style bindinng</h2>

<h2 [style.color] = "text_color">Style bindinng</h2>

<!-- more than one style binding on condition using ngStyle -->

<h2 [ngStyle] = "messageStyle">Style bindinng</h2>


<!-- event binding -->

<button (click) = "Onclick()">button</button><br>
{{buttonClick}}

<!-- Use of $event variable to get all the information about event -->

<button (click) = "OnClick($event)">button</button>
{{buttonClick}}

<!-- without using event Handler -->
<button (click) = "buttonClick = 'without event handler'">button</button>
{{buttonClick}}
<br>


<!-- Template Reference Variables -->
<input type="text" #inputReference>
<button (click) = "template()"></button>
