import { Component } from '@angular/core';
// import { FormGroup, FormControl } from '@angular/forms';
// import { FormGroup } from '@angular/forms';
import { FormBuilder , Validators } from '@angular/forms';
import { forbiddenNameValidator } from './shared/user-name.validator';
import { PasswordValidator } from './shared/password.validator';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {

  constructor(private fb : FormBuilder){};
 
  registrationForm : any;
  

  get UserName(){
    return this.registrationForm.controls['userName'];
  }

  get Email(){
    return this.registrationForm.controls['email'];
  }

  ngOnInit(){
    this.registrationForm = this.fb.group({
      userName: ["", [Validators.required,Validators.minLength(3),forbiddenNameValidator(/admin/)]],
      email: "",
      subscribe: [false],
      password: [""],
      confirmPassword: [""],
      address: this.fb.group({
        city: [""],
        state: [""],
        postCode: [""]
      })
   },{validator : PasswordValidator})

   this.registrationForm.controls['subscribe'].valueChanges.subscribe((checkedValue : any) => {
      const email = this.registrationForm.controls['email'];
      if(checkedValue){
        email.setValidators(Validators.required);
      }
      else{
        email.clearValidators();
      }
      email.updateValueAndValidity();
    });
  }

   

//     registrationForm = new FormGroup(
//       {
//         userName: new FormControl(""),
//         password: new FormControl(""),
//         confirmPassword: new FormControl(""),
//         address: new FormGroup({
//           city: new FormControl(""),
//           state: new FormControl(""),
//           postCode: new FormControl("")
//         })
//       }
//     )

    OnLoad(){
      this.registrationForm.setValue({
        userName: "rutvik",
        email: "",
        subscribe: [false],
        password: "test",
        confirmPassword: "test",
        address: {
          city: "Ahmedabad",
          state: "gujrat",
          postCode: "380026",

      }
    })
}
}
<!DOCTYPE html>
<html lang="en">
  <head>
    <meta charset="UTF-8" />
    <meta http-equiv="X-UA-Compatible" content="IE=edge" />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <title>Document</title>
  </head>
  <body>
    <div class="container-fluid">
      <h2>Registration Form</h2>
      <form [formGroup]="registrationForm">
        {{ registrationForm.value | json }}

        <div class="form-group">
          <label>Username</label>
          <input
            [class.is-invalid]="
            UserName.invalid &&
              UserName.touched
            "
            formControlName="userName"
            type="text"
            class="form-control"
          />
        </div>

        <div
          *ngIf="
            UserName.invalid &&
            UserName.touched
          "
          class="text-danger mb-3"
        >
          <small *ngIf="UserName.errors?.['required']"
            >UserName is required</small
          >
          <small
            *ngIf="UserName.errors?.['minlength']"
          >
            UserName must be at least 3 characters</small
          >
          <small *ngIf = "UserName.errors?.['forbiddenName']">
            {{UserName.errors?.['forbiddenName'].value}} UserName is not allowed
          </small>
        </div>

        <div class="form-group">
          <label>Email</label>
          <input type="email"  [class.is-invalid] = "Email.invalid && Email.touched"  formControlName="email" class="form-control">
          <small [class.d-none] = "Email.untouched || Email.valid">Please Enter </small>
        </div>
  
        <div class="form-check">
          <label class="form-check-label">
            Send me promotional offers
          </label>
          <input type="checkbox" class="form-check-input" formControlName="subscribe">
        </div>

        <div class="form-group mb-3">
          <label>Password</label> 
          <input
            formControlName="password"
            type="password"
            class="form-control"
          />
        </div>

        <div class="form-group mb-3">
          <label>Confirm Password</label>
          <input
            formControlName="confirmPassword"
            type="password"
            class="form-control"
            [class.is-invalid] = "registrationForm.errors?.['misMatch']"
          />
          <small *ngIf = "registrationForm.errors?.['misMatch']" class="text-danger">Password do not match</small>
        </div>

        <div formGroupName="address" class="mb-3">
          <div class="form-group">
            <label>City</label>
            <input formControlName="city" type="text" class="form-control" />
          </div>
          <div class="form-group">
            <label>State</label>
            <input formControlName="state" type="text" class="form-control" />
          </div>
          <div class="form-group">
            <label>Postal Code</label>
            <input
              formControlName="postCode"
              type="text"
              class="form-control"
            />
          </div>
        </div>

        <button class="btn btn-primary mb-3" type="submit">Register</button
        ><br />
        <button class="btn btn-info" type="button" (click)="OnLoad()">
          Load Api Data
        </button>
      </form>
    </div>
  </body>
</html>
